/**
 * flyfile.js
 */

const paths = {
	scripts: [ 'src/**/*.js' ],
	dest: './'
};
const babel = {
	options: {
		stage: 0,
		sourceMaps: true
	}
};

export default function *(){
	yield this.start(['clean', 'build'], { paralle: false });
}

export function *watch(){
	yield this.watch([ paths.scripts ], [ 'default' ]);
}

export function *clean(){
	yield this.clear('lib');
}

export function *build(){
	yield this
		.source(paths.scripts)
		.babel(babel.options)
		.target(paths.dest);
}
